    }


    return(
    <div>
        <form action="">
            <input type="text" value={profile.name} onChange={onName}/>
            <input type="number" value={profile.age} onChange={onNumber}/>
            <h2>Name : {profile.name} age: {profile.age}</h2>
        </form>
    </div>
)





}
